<?phpclass Attaque_Model_Journal{    protected $_jo_id;    protected $_jo_id_attaque;    protected $_jo_id_defense;	protected $_jo_login_attaque;	protected $_jo_login_defense;    protected $_jo_id_victoire;    protected $_jo_or_pille;    protected $_jo_bois_pille;    protected $_jo_population_pille;    protected $_jo_mana_pille;    protected $_jo_date;     public function __construct(array $options = null)    {        if (is_array($options)) {            $this->setOptions($options);        }    }    public function __set($name, $value)    {        if ( strpos( $name, '_' ) ) {            $method = explode( '_' , $name );            $method = 'set' . ucfirst( $method[1] );        }        else {             $method = 'set' . $name;        }        if (('mapper' == $name) || !method_exists($this, $method)) {            throw new Exception('Invalid Unite property');        }        $this->$method($value);    }    public function __get($name)    {        if ( strpos( $name, '_' ) ) {            $method = explode( '_' , $name );            $method = 'get' . ucfirst( $method[1] );        }        else {             $method = 'get' . $name;        }           if (('mapper' == $name) || !method_exists($this, $method)) {            throw new Exception('Invalid Unite property');        }        return $this->$method();    }     public function setOptions(array $options)    {        $methods = get_class_methods($this);        foreach ($options as $key => $value) {            $method = 'set' . ucfirst($key);            if (in_array($method, $methods)) {                $this->$method($value);            }        }        return $this;    }     public function setIdAttaque($text)    {        $this->_jo_id_attaque = (string) $text;        return $this;    }    public function getIdAttaque()    {        return $this->_jo_id_attaque;    }		public function setIdVictoire($text)    {        $this->_jo_id_victoire = (string) $text;        return $this;    }    public function getIdVictoire()    {        return $this->_jo_id_victoire;    }     public function setIdDefense($text)    {        $this->_jo_id_defense = (string) $text;        return $this;    }    public function getIdDefense()    {        return $this->_jo_id_defense;    }        public function setOrPille($price)    {        $this->_jo_or_pille = (float) $price;        return $this;    }    public function getOrPille()    {        return $this->_jo_or_pille;    }        public function setBoisPille($id)    {        $this->_jo_bois_pille = (int) $id;        return $this;    }    public function getBoisPille()    {        return $this->_jo_bois_pille;    }        public function setPopulationPille($id)    {        $this->_jo_population_pille = (int) $id;        return $this;    }     public function getPopulationPille()    {        return $this->_jo_population_pille;    }                    public function setManaPille($id)    {        $this->_jo_mana_pille = (int) $id;        return $this;    }     public function getManaPille()    {        return $this->_jo_mana_pille;    }        public function setDate($id)    {        $this->_jo_date = (int) $id;        return $this;    }     public function getDate()    {        return $this->_jo_date;    }		public function setLoginAttaque($id)    {        $this->_jo_login_attaque = (int) $id;        return $this;    }     public function getLoginAttaque()    {        return $this->_jo_login_attaque;    }		public function setLoginDefense($id)    {        $this->_jo_login_defense = (int) $id;        return $this;    }     public function getLoginDefense()    {        return $this->_jo_login_defense;    }           }