<?phpclass Cms_Model_Static{     protected $_cs_id;    protected $_cs_headTitle;    protected $_cs_description;    protected $_cs_keywords;    protected $_cs_title;    protected $_cs_content;     public function __construct(array $options = null)    {        if (is_array($options)) {            $this->setOptions($options);        }    }    public function __set($name, $value)    {        //$method = 'set' . $name;        $method = explode( '_' , $name );        $method = 'set' . ucfirst( $method[2] );        if (('mapper' == $name) || !method_exists($this, $method)) {            throw new Exception('Invalid static property');        }        $this->$method($value);    }    public function __get($name)    {        //$method = 'get' . $name;        if ( strpos( $name, '_' ) ) {            $method = explode( '_' , $name );            $method = 'set' . ucfirst( $method[2] );        }        else {             $method = $name;        }        if (('mapper' == $name) || !method_exists($this, $method)) {            throw new Exception('Invalid static property');        }        return $this->$method();    }     public function setOptions(array $options)    {        $methods = get_class_methods($this);        foreach ($options as $key => $value) {            $method = 'set' . ucfirst($key);            if (in_array($method, $methods)) {                $this->$method($value);            }        }        return $this;    }     public function setHeadtitle($text)    {        $this->_cs_headtitle = (string) $text;        return $this;    }    public function getHeadtitle()    {        return $this->_cs_headtitle;    }     public function setDescription($text)    {        $this->_cs_description = (string) $text;        return $this;    }        public function getDescription()    {        return $this->_cs_description;    }    public function setKeywords($text)    {        $this->_cs_keywords = (string) $text;        return $this;    }     public function getKeywords()    {        return $this->_cs_keywords;    }     public function setTitle($text)    {        $this->_cs_title = (string) $text;        return $this;    }     public function getTitle()    {        return $this->_cs_title;    }    public function setContent($text)    {        $this->_cs_content = (string) $text;        return $this;    }     public function getContent()    {        return $this->_cs_content;    }        public function setId($id)    {        $this->_cs_id = (int) $id;        return $this;    }     public function getId()    {        return $this->_cs_id;    }}    