<?phpclass Produit_Model_UserBatiment {    protected $_ub_user_id;    protected $_ub_produit_batiment_id;    protected $_ub_quantite;    protected $_ub_user_duree;    protected $_ub_structure;     public function __construct(array $options = null)    {        if (is_array($options)) {            $this->setOptions($options);        }    }            public function setOptions(array $options)    {        $methods = get_class_methods($this);        foreach ($options as $key => $value) {            $method = 'set' . ucfirst($key);            if (in_array($method, $methods)) {                $this->$method($value);            }        }        return $this;    }     public function setQuantite($text)    {        $this->_ub_quantite = (string) $text;        return $this;    }    public function getQuantite()    {        return $this->_ub_quantite;    }            public function setProduitBatimentId($id)    {        $this->_ub_produit_batiment_id = (int) $id;        return $this;    }     public function getProduitBatimentId()    {        return $this->_ub_produit_batiment_id;    }        public function setUserId($id)    {        $this->_ub_user_id = (int) $id;        return $this;    }     public function getUserId()    {        return $this->_ub_user_id;    }        public function setDuree($id)    {        $this->_ub_user_duree = (int) $id;        return $this;    }     public function getDuree()    {        return $this->_ub_user_duree;    }            public function setStructure($id)    {        $this->_ub_structure = (int) $id;        return $this;    }     public function getStructure()    {        return $this->_ub_structure;    }}