<?phpclass Produit_Model_UserMagie{    protected $_um_user_id;    protected $_um_magie_id;    protected $_um_quantite;    protected $_um_duree;    protected $_um_cible_id;     public function __construct(array $options = null)    {        if (is_array($options)) {            $this->setOptions($options);        }    }            public function setOptions(array $options)    {        $methods = get_class_methods($this);        foreach ($options as $key => $value) {            $method = 'set' . ucfirst($key);            if (in_array($method, $methods)) {                $this->$method($value);            }        }        return $this;    }     public function setQuantite($text)    {        $this->_um_quantite = (string) $text;        return $this;    }    public function getQuantite()    {        return $this->_um_quantite;    }            public function setMagieId($id)    {        $this->_um_magie_id = (int) $id;        return $this;    }     public function getMagieId()    {        return $this->_um_magie_id;    }        public function setUserId($id)    {        $this->_um_user_id = (int) $id;        return $this;    }     public function getUserId()    {        return $this->_um_user_id;    }        public function setDuree($id)    {        $this->_um_duree = (int) $id;        return $this;    }     public function getDuree()    {        return $this->_um_duree;    }            public function setCibleId($id)    {        $this->_um_cible_id = (int) $id;        return $this;    }     public function getCibleId()    {        return $this->_um_cible_id;    }}