<?phpclass Produit_Model_UserUnite {    protected $_uu_user_id;    protected $_uu_produit_unite_id;    protected $_uu_quantite;    protected $_uu_user_duree;    protected $_uu_en_cours;     public function __construct(array $options = null)    {        if (is_array($options)) {            $this->setOptions($options);        }    }            public function setOptions(array $options)    {        $methods = get_class_methods($this);        foreach ($options as $key => $value) {            $method = 'set' . ucfirst($key);            if (in_array($method, $methods)) {                $this->$method($value);            }        }        return $this;    }     public function setQuantite($text)    {        $this->_uu_quantite = (string) $text;        return $this;    }    public function getQuantite()    {        return $this->_uu_quantite;    }            public function setProduitUniteId($id)    {        $this->_uu_produit_unite_id = (int) $id;        return $this;    }     public function getProduitUniteId()    {        return $this->_uu_produit_unite_id;    }        public function setUserId($id)    {        $this->_uu_user_id = (int) $id;        return $this;    }     public function getUserId()    {        return $this->_uu_user_id;    }        public function setDuree($id)    {        $this->_uu_user_duree = (int) $id;        return $this;    }     public function getDuree()    {        return $this->_uu_user_duree;    }            public function setEnCours($id)    {        $this->_uu_en_cours = (int) $id;        return $this;    }     public function getEnCours()    {        return $this->_uu_en_cours;    }}